import React from 'react';

import  { useState } from 'react';
import "./BlockChain.css";

const BlockChain = () => {
 const quizQuestions = [
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 2: Who painted the Mona Lisa?',
      answers: {
        a: 'Leonardo da Vinci',
        b: 'Pablo Picasso',
        c: 'Vincent van Gogh',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },

    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    },
    {
      question: 'Question 1: What is the capital of France?',
      answers: {
        a: 'Paris',
        b: 'London',
        c: 'Berlin',
      },
      correctAnswer: 'a',
    }

    // Add more questions...
  ];

  const [userAnswers, setUserAnswers] = useState({});
  const [showResult, setShowResult] = useState(false);
  const totalQuestions = quizQuestions.length;

  const displayQuizQuestions = () => {
    return quizQuestions.map((question, index) => {
      return (
        <div key={index} className="mb-4">
          <p>{question.question}</p>
          {Object.keys(question.answers).map((option) => (
            <label key={option} className="block">
              <input
                type="radio"
                name={`question-${index}`}
                value={option}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
              />
              <span className="ml-2">{option}: {question.answers[option]}</span>
            </label>
          ))}
        </div>
      );
    });
  };

  const handleAnswerChange = (questionIndex, selectedAnswer) => {
    setUserAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionIndex]: selectedAnswer,
    }));
  };

  const calculateScore = () => {
    let score = 0;

    for (let i = 0; i < totalQuestions; i++) {
      if (userAnswers[i] === quizQuestions[i].correctAnswer) {
        score++;
      }
    }

    return score;
  };

  const displayQuizResults = () => {
    const score = calculateScore();
    const percentage = (score / totalQuestions) * 100;

    let result = `You scored ${score}/${totalQuestions}. `;
    result += `(${percentage}%) `;

    if (percentage >= 70) {
      result += 'Congratulations! You passed the quiz.';
    } else {
      result += 'Sorry, you failed the quiz.';
    }

    return <div className="mt-4">{result}</div>;
  };

  const handleSubmit = () => {
    setShowResult(true);
  };

  return (
    <div className="max-w-md mx-auto p-4 bg-slate-400">
      <h1 className="text-2xl font-bold mb-4">Quiz</h1>
      <div className="mb-4">{displayQuizQuestions()}</div>
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={handleSubmit}>
        Submit
      </button>
      {showResult && displayQuizResults()}
    </div>
  );
};

export default BlockChain;
